DROP TABLE IF EXISTS `admin_user`;
CREATE TABLE `admin_user` (
  `admin_user_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '管理员用户ID',
  `email` varchar(64) NOT NULL DEFAULT '' COMMENT '用户名',
  `password` char(60) NOT NULL DEFAULT '' COMMENT '密码, 采用password_*系列函数',
  `real_name` varchar(32) NOT NULL COMMENT '真实姓名',
  `mobile` varchar(24) NOT NULL COMMENT '手机号',
  `avatar` char(32) NOT NULL DEFAULT '' COMMENT '用户头像',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否可用: 0:未激活, 1:激活',
  `last_login_ip` varchar(24) NOT NULL COMMENT '上次登录IP',
  `last_login_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上次登录时间',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '生成账号时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户最近一次信息更新时间',
  PRIMARY KEY (`admin_user_id`),
  UNIQUE KEY `email` (`email`),
  KEY `mobile` (`mobile`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='管理员用户表';

